import React, { useEffect, useState } from "react";

function App() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ name: "", email: "", age: "" });
  const [editingId, setEditingId] = useState(null);

  const fetchStudents = async () => {
    const res = await fetch("http://localhost:8081/api/students");
    setStudents(await res.json());
  };

  useEffect(() => { fetchStudents(); }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    if (editingId) {
      await fetch(`http://localhost:8081/api/students/${editingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
    } else {
      await fetch("http://localhost:8081/api/students", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
    }
    setForm({ name: "", email: "", age: "" });
    setEditingId(null);
    fetchStudents();
  };

  const handleEdit = student => {
    setForm({ name: student.name, email: student.email, age: student.age });
    setEditingId(student.id);
  };

  const handleDelete = async id => {
    await fetch(`http://localhost:8081/api/students/${id}`, { method: "DELETE" });
    fetchStudents();
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto" }}>
      <h2>Student Record Management</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
        <input name="email" placeholder="Email" value={form.email} onChange={handleChange} required />
        <input name="age" placeholder="Age" type="number" value={form.age} onChange={handleChange} required />
        <button type="submit">{editingId ? "Update" : "Add"}</button>
        {editingId && <button type="button" onClick={() => { setEditingId(null); setForm({ name: "", email: "", age: "" }); }}>Cancel</button>}
      </form>
      <ul>
        {students.map(s => (
          <li key={s.id}>
            {s.name} ({s.email}, {s.age} years)
            <button type="button" onClick={() => handleEdit(s)}>Edit</button>
            <button type="button" onClick={() => handleDelete(s.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
